# llm_intent_router.py
# LLM Intent Router for Jacques – routes to agents by producing JSON {intent,next,slots}
# Compatible with Langflow Message.text (falls back to .content if needed).

import json
import re
from typing import Any, Dict, List, Optional

from langflow.custom.custom_component.component import Component
from langflow.io import (
    MessageInput,
    HandleInput,
    BoolInput,
    IntInput,
    DropdownInput,
    Output,
)
from langflow.schema.message import Message

# -------------------- helpers (Message compat) --------------------
def _msg_get_text(m: Any) -> str:
    if isinstance(m, Message):
        if getattr(m, "text", None) is not None:
            return str(m.text)
        if getattr(m, "content", None) is not None:
            return str(m.content)
        return ""
    if isinstance(m, str):
        return m
    return "" if m is None else str(m)

def _msg_make(text: str, data: Optional[Dict[str, Any]] = None) -> Message:
    return Message(text=text, data=data or None)

def _coerce_json(s: str) -> Optional[Dict[str, Any]]:
    s = (s or "").strip()
    # direct
    try:
        return json.loads(s)
    except Exception:
        pass
    # extract first {...} block
    try:
        m = re.search(r"\{.*\}", s, re.S)
        if m:
            return json.loads(m.group(0))
    except Exception:
        return None
    return None

# -------------------- judge system prompt --------------------
_SYS_PROMPT_PT = """\
Você é o ROTEADOR do Jacques. Classifique o pedido do usuário e produza um JSON com:
- "intent": (menu | allergy | coupons | concierge | multi | unknown)
- "next": até 3 agentes dentre ["MenuSommelier","AllergyDoctor","CupomAssistant","RestaurantConcierge"]
- "slots": extraídos do texto, sem pedir localização:
  {
    "restaurant_id": null,
    "restaurant_name": null,
    "cuisine": [],
    "allergies": [],
    "price_level": null,        // mapear $, $$, $$$ para 1,2,3 quando explícito
    "neighborhood": null,
    "coupons_interest": null
  }

Regras:
- PRATOS/CARDÁPIO → incluir "MenuSommelier".
- Se houver alergias/intolerâncias → incluir "AllergyDoctor" (geralmente com "MenuSommelier").
- Cupom/promo/desconto → incluir "CupomAssistant".
- Telefone/endereço/horário/site/lista de restaurantes → incluir "RestaurantConcierge".
- Nunca invente dados; nunca peça localização.
- Saída: SOMENTE JSON válido (sem texto fora do JSON).

Exemplos (não inclua na saída real):
Usuário: "pratos mexicanos sem lactose"
→ {"intent":"multi","next":["MenuSommelier","AllergyDoctor"],"slots":{"cuisine":["mexicana"],"allergies":["lactose"],"restaurant_id":null,"restaurant_name":null,"price_level":null,"neighborhood":null,"coupons_interest":null}}

Usuário: "tem cupom para comida mexicana?"
→ {"intent":"coupons","next":["CupomAssistant"],"slots":{"cuisine":["mexicana"],"allergies":[],"restaurant_id":null,"restaurant_name":null,"price_level":null,"neighborhood":null,"coupons_interest":true}}
"""

# -------------------- lightweight keyword fallback --------------------
def _keyword_fallback(text: str) -> Dict[str, Any]:
    t = (text or "").lower()

    slots = {
        "restaurant_id": None,
        "restaurant_name": None,
        "cuisine": [],
        "allergies": [],
        "price_level": None,
        "neighborhood": None,
        "coupons_interest": None,
    }

    # cuisine
    if "mexican" in t or "mexican@" in t or "mexicano" in t or "mexicana" in t:
        slots["cuisine"].append("mexicana")
    if "japonesa" in t or "japonês" in t or "japones" in t or "sushi" in t:
        slots["cuisine"].append("japonesa")
    if "italiana" in t or "italiano" in t or "pizza" in t or "pasta" in t:
        slots["cuisine"].append("italiana")

    # allergies (normalize)
    allergy_terms = [
        "lactose", "glúten", "gluten", "amendoim", "ovos", "ovo", "soja",
        "gergelim", "frutos do mar", "peixe", "leite", "queijo", "manteiga", "creme", "caseína", "caseina"
    ]
    for a in allergy_terms:
        if a in t:
            slots["allergies"].append("glúten" if a == "gluten" else ("caseína" if a == "caseina" else a))

    # price by symbols
    if "$$$" in t:
        slots["price_level"] = 3
    elif "$$" in t:
        slots["price_level"] = 2
    elif "$" in t:
        slots["price_level"] = 1

    # coupons intent
    if any(k in t for k in ["cupom", "cupons", "promo", "promoção", "promocao", "desconto", "voucher", "código", "codigo"]):
        slots["coupons_interest"] = True

    # routes
    next_routes: List[str] = []
    if any(k in t for k in ["prato", "pratos", "menu", "cardápio", "cardapio"]):
        next_routes.append("MenuSommelier")
    if slots["allergies"]:
        if "MenuSommelier" not in next_routes:
            next_routes.append("MenuSommelier")
        next_routes.append("AllergyDoctor")
    if slots["coupons_interest"]:
        next_routes.append("CupomAssistant")
    if any(k in t for k in ["telefone", "endereço", "endereco", "horário", "horario", "site", "website", "restaurante", "restaurantes", "lista"]):
        next_routes.append("RestaurantConcierge")
    if not next_routes and slots["cuisine"]:
        next_routes.append("RestaurantConcierge")

    intent = (
        "menu" if set(next_routes) == {"MenuSommelier"}
        else "coupons" if set(next_routes) == {"CupomAssistant"}
        else "concierge" if set(next_routes) == {"RestaurantConcierge"}
        else "multi" if len(next_routes) > 1
        else "unknown"
    )
    return {"intent": intent, "next": next_routes[:3], "slots": slots}

# -------------------- component --------------------
class LLMIntentRouter(Component):
    display_name = "LLM Intent Router"
    description = "Classifica o pedido e produz JSON de roteamento (next/slots) para os agentes do sistema."
    icon = "git-branch"
    name = "LLMIntentRouter"

    inputs = [
        MessageInput(name="user_message", display_name="User Message", required=True),
        HandleInput(name="llm", display_name="Judge LLM", input_types=["LanguageModel"], required=True),
        BoolInput(name="strict_json", display_name="Strict JSON (recusar se inválido)", value=False),
        BoolInput(name="enable_fallback", display_name="Enable Keyword Fallback", value=True),
        IntInput(name="max_routes", display_name="Max Routes", value=3),
        DropdownInput(
            name="policy",
            display_name="Routing Policy",
            options=["balanced", "menu-first", "concierge-first"],
            value="menu-first",  # default recomendado
        ),
    ]

    outputs = [
        Output(display_name="Router JSON", name="router_json", method="emit_router_json", group_outputs=True),
    ]

    # ---- building prompt for judge LLM
    def _build_prompt(self, text: str) -> List[Dict[str, str]]:
        return [
            {"role": "system", "content": _SYS_PROMPT_PT},
            {"role": "user", "content": f'Usuário:\n"""\n{text}\n"""'},
        ]

    # ---- main
    async def emit_router_json(self) -> Message:
        text = _msg_get_text(self.user_message)
        messages = self._build_prompt(text)
        self.status = "Classificando intenção…"

        try:
            # Call judge LLM (async if available)
            if hasattr(self.llm, "ainvoke"):
                resp = await self.llm.ainvoke(messages)
            else:
                resp = self.llm.invoke(messages)  # type: ignore[call-arg]
            raw = _msg_get_text(resp)

            data = _coerce_json(raw)
            if not data:
                if self.strict_json:
                    raise ValueError("Judge LLM não retornou JSON válido e Strict JSON está habilitado.")
                data = _keyword_fallback(text)

            # ---- normalize shape
            data.setdefault("next", [])
            data.setdefault("slots", {})
            # ensure slots keys
            for k, v in {
                "restaurant_id": None,
                "restaurant_name": None,
                "cuisine": [],
                "allergies": [],
                "price_level": None,
                "neighborhood": None,
                "coupons_interest": None,
            }.items():
                data["slots"].setdefault(k, v)

            # ---- optional policies
            # prefer menu if user asked for dishes/menu
            if self.policy == "menu-first":
                if re.search(r"\b(prato|pratos|menu|card[aá]pio)\b", text.lower()) and "MenuSommelier" not in data["next"]:
                    data["next"] = ["MenuSommelier"] + [r for r in data["next"] if r != "MenuSommelier"]

            if self.policy == "concierge-first":
                if re.search(r"(telefone|endere[cç]o|hor[áa]rio|site|restaurante)", text.lower()) and "RestaurantConcierge" not in data["next"]:
                    data["next"] = ["RestaurantConcierge"] + [r for r in data["next"] if r != "RestaurantConcierge"]

            # ---- HARD FAILSAFE: if allergies present, force AllergyDoctor + MenuSommelier
            slots = data.get("slots", {}) or {}
            if slots.get("allergies"):
                nxt = data.get("next", [])
                if "MenuSommelier" not in nxt:
                    nxt = ["MenuSommelier"] + [r for r in nxt if r != "MenuSommelier"]
                if "AllergyDoctor" not in nxt:
                    nxt.append("AllergyDoctor")
                data["next"] = nxt

            # respect max routes (min 1)
            data["next"] = data["next"][: max(1, int(self.max_routes or 3))]

            # status & return
            self.status = f'intent={data.get("intent","-")} | next={data.get("next",[])}'
            return _msg_make(json.dumps(data, ensure_ascii=False), data)

        except Exception as e:
            self.status = f"router error: {e}"
            fb = _keyword_fallback(text) if self.enable_fallback else {"intent": "unknown", "next": [], "slots": {}}
            return _msg_make(json.dumps(fb, ensure_ascii=False), fb)

