import json
from typing import Any, Dict, List, Optional, Set

from langflow.custom.custom_component.component import Component
from langflow.io import MessageInput, MessageTextInput, BoolInput, Output
from langflow.schema.message import Message


def _try_json(x: Any) -> Optional[Dict[str, Any]]:
    if isinstance(x, Message):
        if isinstance(x.data, dict):
            return x.data
        if x.content:
            try:
                return json.loads(x.content)
            except Exception:
                return None
        return None
    if isinstance(x, dict):
        return x
    if isinstance(x, str) and x.strip():
        try:
            return json.loads(x)
        except Exception:
            return None
    return None


class SwitchRouter(Component):
    """
    Switch Router – fan-out by Router JSON.
    Reads {"intent": "...", "next": ["MenuSommelier", ...], "slots": {...}}
    and forwards the original user message to selected agent outputs.

    Outputs:
      - to_sommelier
      - to_concierge
      - to_none          (no route selected)
      - router_json_out  (echo/debug)
    """
    display_name = "Switch Router"
    description = "Routes the user message to selected agents based on Router JSON (intent/next/slots)."
    icon = "split"
    name = "SwitchRouter"

    # Inputs
    inputs = [
        MessageInput(
            name="user_message",
            display_name="User Message",
            info="Original message from Chat Input.",
            required=True,
        ),
        MessageInput(
            name="router_message",
            display_name="Router JSON",
            info='LLM output with {"intent": "...", "next": ["MenuSommelier", ...], "slots": {...}}',
            required=False,
        ),
        MessageTextInput(
            name="force_routes_csv",
            display_name="Force Routes (CSV)",
            info='Override routes regardless of router JSON. Ex.: "MenuSommelier,RestaurantConcierge"',
            required=False,
        ),
        BoolInput(
            name="auto_from_intent",
            display_name="Auto-map from intent when next is missing",
            value=True,
        ),
        BoolInput(
            name="strict_agent_names",
            display_name="Strict agent names (ignore aliases)",
            value=False,
        ),
        BoolInput(
            name="pass_router_json",
            display_name="Pass router JSON in Message.data",
            value=True,
        ),
        BoolInput(
            name="pass_slots",
            display_name="Pass slots in Message.data",
            value=True,
        ),
    ]

    # Outputs
    outputs = [
        Output(display_name="To Sommelier", name="to_sommelier", method="emit_sommelier", group_outputs=True),
        Output(display_name="To Concierge", name="to_concierge", method="emit_concierge", group_outputs=True),
        Output(display_name="None/Else", name="to_none", method="emit_none", group_outputs=True),
        Output(display_name="Router JSON (echo)", name="router_json_out", method="emit_router_json", group_outputs=True),
    ]

    # internal state
    _chosen: Set[str] = set()
    _router_payload: Dict[str, Any] = {}
    _slots: Dict[str, Any] = {}

    def _normalize_routes(self, requested: List[str]) -> Set[str]:
        # canonical names
        canon = {
            "MenuSommelier": "sommelier",
            "RestaurantConcierge": "concierge",
        }
        if self.strict_agent_names:
            return {canon[r] for r in requested if r in canon}

        # aliases (lowercased)
        aliases = {
            "sommelier": "sommelier",
            "menusommelier": "sommelier",
            "menu_sommelier": "sommelier",
            "menu": "sommelier",
            "dish": "sommelier",
            "prato": "sommelier",

            "concierge": "concierge",
            "restaurantconcierge": "concierge",
            "restaurante": "concierge",
            "detalhes": "concierge",
            "details": "concierge",
            "lista": "concierge",
            "list": "concierge",
        }
        aliases.update({k.lower(): v for k, v in {
            "MenuSommelier": "sommelier",
            "RestaurantConcierge": "concierge",
        }.items()})

        norm: Set[str] = set()
        for r in requested:
            key = (r or "").strip().lower()
            if key in aliases:
                norm.add(aliases[key])
        return norm

    def _routes_from_intent(self, intent: Optional[str]) -> Set[str]:
        if not intent or not self.auto_from_intent:
            return set()
        i = intent.lower()
        # Map only menu/concierge keywords — no coupons/allergy in this flow
        if any(w in i for w in ["dish", "prato", "menu", "cardápio", "cardapio"]):
            return {"sommelier"}
        if any(w in i for w in ["concierge", "restaurante", "detalhe", "details", "lista", "list"]):
            return {"concierge"}
        # Default: no route
        return set()

    def _build_common_message(self) -> Message:
        data: Dict[str, Any] = {}
        if self.pass_router_json and self._router_payload:
            data["router"] = self._router_payload
        if self.pass_slots and self._slots:
            data["slots"] = self._slots
        return Message(content=self.user_message.content, data=data or None)

    def _pre_run_setup(self):
        # parse incoming router JSON
        self._router_payload = _try_json(self.router_message) or {}
        self._slots = self._router_payload.get("slots") or {}
        next_list: List[str] = []

        # 1) router.next
        if isinstance(self._router_payload.get("next"), list):
            next_list = [str(x) for x in self._router_payload["next"] if x is not None]

        # 2) override
        if (self.force_routes_csv or "").strip():
            forced = [s.strip() for s in self.force_routes_csv.split(",") if s.strip()]
            next_list = forced

        # 3) derive from intent
        if not next_list:
            next_list = list(self._routes_from_intent(self._router_payload.get("intent")))

        self._chosen = self._normalize_routes(next_list)

        # status for debugging
        intent = self._router_payload.get("intent")
        self.status = f"intent={intent or '-'} | routes={sorted(self._chosen) or ['none']}"

    # outputs
    def emit_sommelier(self) -> Message:
        return self._build_common_message() if "sommelier" in self._chosen else Message(content="")

    def emit_concierge(self) -> Message:
        return self._build_common_message() if "concierge" in self._chosen else Message(content="")

    def emit_none(self) -> Message:
        # emit message (with router/slots) when nothing is chosen so downstream can decide fallback
        if not self._chosen:
            return self._build_common_message()
        return Message(content="")

    def emit_router_json(self) -> Message:
        payload = self._router_payload or {}
        return Message(content=json.dumps(payload, ensure_ascii=False), data=payload or None)

# artifacts_merger.py
# Merges artifacts/citations/slots for Jacques Talker with a MENU-ONLY flow.
# - Accepts MenuSommelier outputs (new or legacy) and maps everything to:
#     artifacts.recommendations[] (+ pagination, applied_filters, selection)
# - Optionally merges RestaurantConcierge outputs (candidates, selection).

import json
from typing import Any, Dict, List, Optional, Tuple

from langflow.custom.custom_component.component import Component
from langflow.io import MessageInput, Output
from langflow.schema.message import Message


# ---------- helpers ----------
def _msg_get_text(m: Any) -> str:
    if isinstance(m, Message):
        if getattr(m, "text", None) is not None:
            return str(m.text)
        if getattr(m, "content", None) is not None:
            return str(m.content)
        return ""
    if isinstance(m, str):
        return m
    return "" if m is None else str(m)


def _take_json(msg: Any) -> Optional[Dict[str, Any]]:
    """Accepts Message (text/content or data), dict or JSON string; returns dict or None."""
    if isinstance(msg, Message):
        txt = _msg_get_text(msg)
        if txt.strip():
            try:
                return json.loads(txt)
            except Exception:
                pass
        if isinstance(msg.data, dict):
            return msg.data
        return None
    if isinstance(msg, dict):
        return msg
    if isinstance(msg, str) and msg.strip():
        try:
            return json.loads(msg)
        except Exception:
            return None
    return None


def _non_null_keys(d: Dict[str, Any]) -> int:
    return sum(1 for v in d.values() if v not in (None, [], {}))


def _best(a: Dict[str, Any] | None, b: Dict[str, Any] | None) -> Dict[str, Any] | None:
    """Pick the richer dict (more non-null keys, then higher 'score')."""
    if not a:
        return b
    if not b:
        return a
    ka, kb = _non_null_keys(a), _non_null_keys(b)
    if ka != kb:
        return a if ka > kb else b
    sa, sb = float(a.get("score") or 0), float(b.get("score") or 0)
    return a if sa >= sb else b


def _dedupe(items: List[Dict[str, Any]], key_fn) -> List[Dict[str, Any]]:
    """Dedupe with 'best' selection per key."""
    keep: Dict[Tuple, Dict[str, Any]] = {}
    for it in items or []:
        k = key_fn(it)
        keep[k] = _best(keep.get(k), it)
    return list(keep.values())


def _dedupe_citations(cits: List[Dict[str, Any]]) -> List[Dict[str, Any]]:
    seen = set()
    out = []
    for c in cits or []:
        if not isinstance(c, dict):
            continue
        key = (c.get("collection"), c.get("doc_id"), c.get("page"))
        if key not in seen:
            seen.add(key)
            out.append(c)
    return out


def _extend(dst: List, src: Optional[List]):
    if isinstance(src, list) and src:
        dst.extend(src)


def _merge_filters(dst: Dict[str, Any], src: Optional[Dict[str, Any]]) -> Dict[str, Any]:
    """Union/override strategy for applied_filters."""
    if not isinstance(src, Dict):
        return dst
    # cuisines: union unique (case-insensitive)
    if isinstance(src.get("cuisine"), list):
        cur = dst.get("cuisine") or []
        cur_set = {str(x).lower() for x in cur}
        for v in src["cuisine"]:
            if str(v).lower() not in cur_set:
                cur.append(v)
                cur_set.add(str(v).lower())
        dst["cuisine"] = cur
    # simple overwrite for common keys when present
    for k in ["restaurant_ids", "restaurant_name", "budget_brl"]:
        if src.get(k) not in (None, "", []):
            dst[k] = src[k]
    return dst


def _mk_uid(restaurant_name: Optional[str], name: Optional[str]) -> str:
    def norm(s: Optional[str]) -> str:
        t = (s or "").strip().lower()
        # remove accents/punctuation simplistic (good enough for IDs)
        repl = str.maketrans(
            "áàãâäéèêëíìîïóòõôöúùûüçñ",
            "aaaaaeeeeiiiiooooouuuucn"
        )
        t = t.translate(repl)
        keep = [ch for ch in t if ch.isalnum() or ch in (" ", "_", "-")]
        return "".join(keep).replace(" ", "_")
    return f"{norm(restaurant_name)}||{norm(name)}"


def _map_items_to_recommendations(items: List[Dict[str, Any]]) -> List[Dict[str, Any]]:
    recs: List[Dict[str, Any]] = []
    for it in items or []:
        name = it.get("name") or it.get("title")
        rest_name = it.get("restaurant_name") or it.get("restaurant")
        recs.append({
            "uid": it.get("uid") or _mk_uid(rest_name, name),
            "type": it.get("type") or "dish",
            "name": name,
            "price_brl": it.get("price_brl"),
            "section": it.get("section"),
            "diet_tags": it.get("diet_tags") or [],
            "restaurant_id": it.get("restaurant_id"),
            "restaurant_name": rest_name,
            "description": it.get("description"),
            "score": it.get("score"),
            "citations": it.get("citations") or [],
        })
    return recs


def _map_sommelier_to_artifacts(part: Dict[str, Any]) -> Dict[str, Any]:
    """
    Normalize Sommelier outputs to a single 'artifacts' dict with:
      - recommendations[] (list of dishes/drinks/restaurants)
      - pagination, page_uids, applied_filters, selection (when present)
    Supports shapes:
      • {"artifacts":{"recommendations":[...]}}
      • {"items":[...]} (legacy; mapped to recommendations)
      • {"artifacts":{"dish_options":[...]}} (legacy; mapped)
    """
    artifacts: Dict[str, Any] = {"intent": "menu"}

    if isinstance(part.get("artifacts"), dict):
        arts = part["artifacts"]
        # recommendations (preferred)
        recs = arts.get("recommendations")
        if isinstance(recs, list):
            artifacts["recommendations"] = recs
        # legacy dish_options -> recommendations
        elif isinstance(arts.get("dish_options"), list):
            artifacts["recommendations"] = _map_items_to_recommendations(arts["dish_options"])

        # passthrough of other useful fields
        for k in ["pagination", "page_uids", "selection", "applied_filters"]:
            if arts.get(k) not in (None, "", []):
                artifacts[k] = arts[k]

    # legacy flat shape with "items"
    if artifacts.get("recommendations") is None and isinstance(part.get("items"), list):
        artifacts["recommendations"] = _map_items_to_recommendations(part["items"])
        # try to synthesize applied_filters if provided
        if isinstance(part.get("applied_filters"), dict):
            artifacts["applied_filters"] = part["applied_filters"]

    # default empty structures
    artifacts.setdefault("recommendations", [])
    artifacts.setdefault("applied_filters", {"cuisine": []})
    return artifacts


# ---------- component ----------
class ArtifactsMerger(Component):
    display_name = "Artifacts Merger"
    name = "ArtifactsMerger"
    icon = "git-merge"
    description = "Mescla artifacts/citations/slots de MenuSommelier (+Concierge opcional) em um único JSON para o Jacques."

    inputs = [
        MessageInput(name="sommelier", display_name="From MenuSommelier", required=False),
        MessageInput(name="concierge", display_name="From RestaurantConcierge", required=False),
        MessageInput(name="router", display_name="Router JSON (opcional)", required=False),
    ]

    outputs = [
        Output(display_name="Merged JSON", name="merged", method="run", group_outputs=True),
    ]

    def run(self) -> Message:
        parts_raw: List[Dict[str, Any]] = []
        for inp in [self.sommelier, self.concierge]:
            j = _take_json(inp)
            if isinstance(j, dict):
                parts_raw.append(j)

        merged_artifacts: Dict[str, Any] = {}
        merged_citations: List[Dict[str, Any]] = []
        merged_slots: Dict[str, Any] = {}
        intents: List[str] = []
        applied_filters: Dict[str, Any] = {"cuisine": []}

        # Normalize Sommelier-only outputs that come without "artifacts"
        normalized_parts: List[Dict[str, Any]] = []
        for p in parts_raw:
            if isinstance(p.get("artifacts"), dict):
                normalized_parts.append(p)
            elif isinstance(p.get("items"), list):
                # Create a synthetic "artifacts" from legacy items
                synthetic = {
                    "artifacts": _map_sommelier_to_artifacts(p),
                    "citations": [],
                    "slots": p.get("slots") or {},
                }
                # Convert "sources" to citations hinting "restaurant_menus"
                sources = p.get("sources") or []
                if isinstance(sources, list):
                    syn_cits = []
                    for s in sources:
                        if isinstance(s, dict):
                            syn_cits.append({
                                "collection": "restaurant_menus",
                                "doc_id": s.get("doc_id"),
                                "page": s.get("page"),
                                "score": s.get("score"),
                            })
                    synthetic["citations"] = syn_cits
                normalized_parts.append(synthetic)
            else:
                # Unknown shape; keep as-is (may contain citations/slots only)
                normalized_parts.append(p)

        # Collect everything
        for p in normalized_parts:
            arts = p.get("artifacts") or {}
            if not isinstance(arts, dict):
                continue

            # recommendations
            _extend(merged_artifacts.setdefault("recommendations", []), arts.get("recommendations"))
            # concierge: candidates / selection passthrough
            _extend(merged_artifacts.setdefault("candidates", []), arts.get("candidates"))

            # singletons (keep richer)
            for key in ["selection", "pagination", "page_uids"]:
                cur = merged_artifacts.get(key)
                cand = arts.get(key)
                if cand in (None, "", []):
                    continue
                if cur is None:
                    merged_artifacts[key] = cand
                else:
                    # for pagination/page_uids just keep the latest; for selection prefer richer
                    if key == "selection" and isinstance(cand, dict) and isinstance(cur, dict):
                        merged_artifacts[key] = _best(cur, cand)
                    else:
                        merged_artifacts[key] = cand

            # intents and filters
            if arts.get("intent"):
                intents.append(str(arts["intent"]))
            if isinstance(arts.get("applied_filters"), dict):
                applied_filters = _merge_filters(applied_filters, arts["applied_filters"])

            # citations: global
            _extend(merged_citations, p.get("citations"))
            # citations inside lists
            for list_key in ["recommendations", "candidates"]:
                for item in arts.get(list_key, []) or []:
                    _extend(merged_citations, item.get("citations"))

            # slots: last non-empty wins
            if isinstance(p.get("slots"), dict):
                for k, v in p["slots"].items():
                    if v not in (None, "", [], {}):
                        merged_slots[k] = v

        # ---------- dedupe phase ----------
        # recommendations: by uid OR (restaurant_name,name lower)
        if "recommendations" in merged_artifacts:
            merged_artifacts["recommendations"] = _dedupe(
                merged_artifacts["recommendations"],
                lambda d: (
                    (d.get("uid") or "").strip().lower()
                    or ((d.get("restaurant_name") or "").strip().lower(), (d.get("name") or "").strip().lower())
                ),
            )

        # candidates: by (id or name lower)
        if "candidates" in merged_artifacts:
            merged_artifacts["candidates"] = _dedupe(
                merged_artifacts["candidates"],
                lambda c: ((c.get("id") or "").strip().lower() or (c.get("name") or "").strip().lower(),),
            )

        # applied_filters + intent synthesis
        merged_artifacts["applied_filters"] = applied_filters or {}
        uniq_intents = {i for i in intents if i}
        merged_artifacts["intent"] = list(uniq_intents)[0] if len(uniq_intents) == 1 else ("menu" if not uniq_intents else "multi")

        # dedupe citations
        merged_citations = _dedupe_citations(merged_citations)

        # also merge router slots if provided
        rj = _take_json(self.router)
        if isinstance(rj, dict) and isinstance(rj.get("slots"), dict):
            for k, v in rj["slots"].items():
                if v not in (None, "", [], {}):
                    merged_slots[k] = v

        # payload
        payload = {
            "artifacts": merged_artifacts,
            "citations": merged_citations,
            "slots": merged_slots or None,
            "plan_id": None,
        }

        # status (human-friendly)
        counts = []
        for k in ["recommendations", "candidates"]:
            n = len(merged_artifacts.get(k, []) or [])
            if n:
                counts.append(f"{k}={n}")
        sel = " selection=1" if merged_artifacts.get("selection") else ""
        self.status = "merge: " + (", ".join(counts) if counts else "vazio") + sel

        return Message(text=json.dumps(payload, ensure_ascii=False), data=payload)
